DataSet.ClientEvent:
var items = [];
var programas = self.getData();
programas.each(function(p) {
	var type = p.get("name");

	var item = {
		type: type,
		items: p.get("documents")
	};
	items.push(item);
});

bvDocument.set("items", items);

TextEditor.ClientEvent:
window.clearTimeout(self.urlQueryTimeout);
self.urlQueryTimeout = window.setTimeout(function() {
	var key = self.get("value");
	if (key && arg.keyCode == 13) {
		window.location.href = "com.bstek.cms.web.portal.search.SearchResult1.d?value=" + key;
	}
}, 500);

var width = 400;
window.setInterval(function() {
	width = width + 20;
	if (width <= 500) {
		self.set("width", width);
	} else {
		window.clearInterval();
	}
}, 55);

var width = 500;
window.setInterval(function() {
	width = width - 20;
	if (width > 400) {
		self.set("width", width);
	} else {
		window.clearInterval();
	}
}, 55);

Label.ClientEvent:
$(arg.dom).off("click");
$(arg.dom)
	.addClass("programaBtn")
	.click(function() {
		programaPanel.show({
			anchorTarget: self,
			vAlign: "bottom",
			offsetTop: 38,
			offsetLeft: 72
		});			
		return false;
	});

Button.ClientEvent:
if (self.get("caption") ==  "&nbsp;登&nbsp;陆") {
	window.open("bdf3.security.ui.view.Login.d");
} else {
	dorado.MessageBox.confirm("确认退出？", function() {
		window.location.href = "${configure['bdf3.logoutPath']}";		
	});
}

var username = "${loginUsername}";
if (username) {
	self.set("caption", username);
} else {
	self.set("caption", "&nbsp;登&nbsp;陆");
}

BlockView.ClientEvent:
$(arg.dom).empty();
var $h = $("<h4 class='ex header'><i class='fa fa-tag'></i> "+ arg.data.type +"</h4>").appendTo(arg.dom);
var $p = $("<p>更多<i class='fa fa-angle-double-right'></i></p>");
$p.appendTo($h);
$p.click(function() {
	window.location.href = "com.bstek.cms.web.portal.more.MoreDocumentQuery1.d?programaName=" + arg.data.type;
});
var items = arg.data.items || [];
var $ul = $("<ul class='ex items'></ul>").appendTo(arg.dom);
items.each(function(item){		
	var time = new Date(item.get("createDate")).formatDate("Y-m-d");
	
	var showName = null;	
	var name = item.get("name");
	if (name.length > 19) {
		showName = name.substring(0, 18) + "...";
	} else {
		showName = name;
	}
	
	var title = name.replace(/\s/g, ",");	
	var $li = $("<li><div title=" + title + "><div>"+ showName +"</li>" + "<p>"+ time + "</p>").appendTo($ul);			
		$li.click(function() {
			var type = item.get("name");
			var name = type.replace(/%/g, "-");
			window.location.href = "com.bstek.cms.web.portal.text.TextTemplate1.d?name=" + name;
		});

});

//当浏览器宽度改变
window.onresize = function() {  
	var winWidth;
	if (window.innerWidth) {
		winWidth = window.innerWidth;
	} else if ((document.body) && (document.body.clientWidth)) {
		winWidth = document.body.clientWidth; 
	}
	
	if (winWidth > 1100) {
		self.set("lineSize", 2);
		self.set("width", 1075);
		self.set("blockWidth", 510);
		self.get("parent").set("width", 1080);
	} else if (winWidth >= 790 && winWidth <= 1100) {
		self.set("lineSize", 2);
		self.set("width", 780);
		self.set("blockWidth", 370);
		self.get("parent").set("width", 800);
	}  else if (winWidth >= 530 && winWidth < 790) {
		self.set("lineSize", 1);
		self.set("width", 520);
		self.set("blockWidth", 510);
		self.get("parent").set("width", 530);
	}  else if (winWidth >= 370 && winWidth < 530) {
		self.set("lineSize", 1);
		self.set("width", 360);
		self.set("blockWidth", 350);
		self.get("parent").set("width", 370);
	}  else {
		self.set("lineSize", 1);
		self.set("width", 345);
		self.set("blockWidth", 340);
		self.get("parent").set("width", 350);
	}
}

var winWidth;
if (window.innerWidth) {
	winWidth = window.innerWidth;
} else if ((document.body) && (document.body.clientWidth)) {
	winWidth = document.body.clientWidth; 
}
if (winWidth > 2000) {
	self.set("lineSize", 3);
	self.set("width", 1650);
	self.set("blockWidth", 510);
	self.get("parent").set("width", 1700);
} else if (winWidth > 1100 && winWidth <= 2000) {
	self.set("lineSize", 2);
	self.set("width", 1075);
	self.set("blockWidth", 510);
	self.get("parent").set("width", 1080);
} else if (winWidth >= 790 && winWidth <= 1100) {
	self.set("lineSize", 2);
	self.set("width", 780);
	self.set("blockWidth", 370);
	self.get("parent").set("width", 800);
}  else if (winWidth >= 530 && winWidth < 790) {
	self.set("lineSize", 1);
	self.set("width", 520);
	self.set("blockWidth", 510);
	self.get("parent").set("width", 530);
}  else if (winWidth >= 370 && winWidth < 530) {
	self.set("lineSize", 1);
	self.set("width", 360);
	self.set("blockWidth", 350);
	self.get("parent").set("width", 370);
