BlockView.ClientEvent:
$(arg.dom).empty();
var $h = $("<h4 class='ex header'><i class='fa fa-dropbox grey-text'></i><br><h2>"+ arg.data.type +"</h2></h4>").appendTo(arg.dom);
$h.click(function() {
	if (arg.data.type == "首页") {
		window.location.href = "com.bstek.cms.web.portal.Portal1.d";
	} else {
		window.location.href = "com.bstek.cms.web.portal.more.MoreDocumentQuery1.d?programaName=" + arg.data.type;
	}	
});

